  onYouTubeIframeAPIReady(instance) {
  	window.YT = instance;
  	return function() {
  		jQuery('.yt_player').each(function(index) {

	      YT.YTplayer[index] = new YT.Player(jQuery(this)[0], {
	        events: {
	          'onReady' : YT.onPlayerReady,
	          'onStateChange': YT.onPlayerStateChange
	        }
	      });

	      jQuery(this).parent().find('.you_tube_play_button').on('click', function() {
	        YT.YTplayer[index].playVideo();
	      });

	    });
  	}
  };

this.onYouTubeIframeAPIReady = this.onYouTubeIframeAPIReady.bind(this);
      this.exitHandler = this.exitHandler.bind(this);
      this.onPlayerReady = this.onPlayerReady.bind(this);
      this.onPlayerStateChange = this.onPlayerStateChange.bind(this);

onPlayerStateChange(e) {
  let iframe = e.target.h;
  if (( e.data == 1 || e.data == -1 || e.data == 3 )&& !YT.wasFullScreen) {
    var requestFullScreen = iframe.requestFullScreen || iframe.mozRequestFullScreen || iframe.webkitRequestFullScreen;
    if (requestFullScreen) {
      iframe.style.display = 'initial';
      requestFullScreen.bind(iframe)();
    }
    if (document.addEventListener) {
        document.addEventListener('webkitfullscreenchange', YT.exitHandler, false);
        document.addEventListener('mozfullscreenchange', YT.exitHandler, false);
        document.addEventListener('fullscreenchange', YT.exitHandler, false);
        document.addEventListener('MSFullscreenChange', YT.exitHandler, false);
    }
  }
}

export const removeTodo = (user_id, todo_id) => {
  return (dispatch) => {
    return apiCall("delete", `/api/users/${user_id}/todos/${todo_id}`)
      .then(() => dispatch(remove(todo_id)))
      .then(history.push('/'))
      .catch((err) => {
        addError(err.message);
      });
  };
};

import { REMOVE_TODO, LOAD_TODOS, UPDATE_TODO } from '../actionTypes'

const todo = (state = [], action) => {
  switch (action.type) {
    case LOAD_TODOS:
      return [...action.todos];
    case REMOVE_TODO:
      return state.filter(todo => todo._id !== action.id);
    case UPDATE_TODO:
      return state.map(t => {
        if (t._id === action.id) {
          t.done = true;
        };
        return t;
      })

    default:
      return state;
  }
}


export default todo;

exports.updateTodo = async function (req, res, next) {
  try {
    let updatedTodo = await db.Todo.findById(req.params.todo_id);
    await db.Todo.update(
      { _id: updatedTodo },
      {
        $set: {
          done: true,
        }
      }
   );
    return res.status(200).json(updatedTodo);
  } catch (err) {
    return next(err);
  }
};

function mapStateToProps(reduxState) {
