export const query = graphql`
  query getPost($uid: String!) {
    prismic {
      postByUID: post(uid: $uid, lang: "en-us") {
        _meta {
          uid
          firstPublicationDate
        }
        title
        unlisted
        deck
        publish_date
        category {
          ... on PRISMIC_Category {
            title
            _meta {
              uid
            }
          }
        }
        authors {
          author {
            ... on PRISMIC_Person {
              _meta {
                uid
              }
              name
              short_bio
              image
              hide_bio
            }
          }
        }
        feature_image
        preview_image
        related_posts {
          post {
            ... on PRISMIC_Post {
              title
              deck
              _meta {
                uid
              }
              category {
                ... on PRISMIC_Category {
                  title
                }
              }
              feature_image
              preview_image
            }
          }
        }
        body {
          ... on PRISMIC_PostBodyText {
            type
            primary {
              content
            }
          }
          ... on PRISMIC_PostBodyPullquote {
            type
            primary {
              text
            }
          }
          ... on PRISMIC_PostBodyBlock_image {
            type
            primary {
              image
              caption
              credit
            }
          }
          ... on PRISMIC_PostBodyCustom_html {
            type
            primary {
              content
            }
          }
        }
        social_title
        social_description
        social_image
      }
    }
  }
`;

