const postQuery = graphql`
  query getCategoryPosts($first: Int = 12, $after: String, $id: String!) {
    prismic {
        categoryPosts: allPosts(first: $first, after: $after, sortBy: publish_date_DESC, where: {category: $id}) {
            edges {
                node {
                    unlisted
                    deck
                    feature_image
                    publish_date
                    title
                    category {
                        ... on PRISMIC_Category {
                            title
                            description
                        }
                    }
                    preview_image
                    _meta {
                        uid
                    }
                }
            }
        }
    }
  }
`

