export function dispatchPageRequest(langCode, changeRoute, index, currentLang) {
  return { type: "PAGES_REQUESTED", langCode, changeRoute, index, currentLang };
}
export function dispatchLandingDataRequest(
  langCode,
  changeRoute,
  index,
  currentLang
) {
  return { type: "LANDING_DATA", langCode, changeRoute, index, currentLang };
}
export function dispatchNavRequest() {
  return { type: "NAVTEXT_REQUESTED" };
}
export function dispatchGlossaryRequest(langCode) {
  return { type: "GLOSSARY_REQUESTED", langCode };
}
export function dispatchSectionRequest(id, langCode, index) {
  return { type: "SECTION_REQUESTED", id, langCode, index };
}
export function dispatchQuizSectionRequest(id, langCode, index) {
  return { type: "QUIZ_SECTION_REQUESTED", id, langCode, index };
}

export function* fetchPages(action) {
  let lang = "en-us";
  if (action.langCode) {
    switch (action.langCode) {
      case "es":
        lang = "es-es";
        break;
      case "fr":
        lang = "fr-fr";
        break;
      case "ar":
        lang = "ar-ae";
        break;
      case "ru":
        lang = "ru";
        break;
      case "he":
        lang = "he";
        break;
      case "bu":
        lang = "bu-my";
        break;
      case "zh":
        lang = "zh-cn";
        break;
      case "da":
        lang = "da-dk";
        break;
      case "de":
        lang = "de-de";
        break;
      case "hi":
        lang = "hi-in";
        break;
      case "id":
        lang = "id";
        break;
      case "ja":
        lang = "ja-jp";
        break;
      case "pt":
        lang = "pt-br";
        break;
      case "sv":
        lang = "sv-se";
        break;
      case "tr":
        lang = "tr";
        break; 
    }
  }

onMouseEnter(event) {
  const langCode = event.target.id;
  if (this.props.landingData) {
    const langData = this.props.landingData.filter((data) => {
      if (data.lang === langCode) {
        return data;
      }
    });
    this.setState({
      upper_text: langData[0].data.description[0].text,
      title: langData[0].data.title,
      manipulated_image: langData[0].data.manipulated_image.url,
      sponsored_by: langData[0].data.sponsored_by,
    });
  }
}

onMouseLeave(event) {
  this.setState({
    ...this.state,
    upper_text: this.props.page.data.description[0].text,
    title: this.props.page.data.title,
    manipulated_image: this.props.page.data.manipulated_image.url,
    sponsored_by: this.props.page.data.sponsored_by,
    landingData: this.props.page.data.landingData,
    id: this.props.page.lang,
  });
}

componentDidUpdate(prevProps) {
  if (
    this.props.hasClicked !== prevProps.hasClicked ||
    this.props.langCode !== prevProps.langCode
  ) {
    if (this.props.hasClicked) {
      scrollIntoView(document.getElementById("intro"), {
        time: 3000,
      });
    }
  }
  if (this.state.video === "" && this.props.page.data !== undefined) {
    this.setState({
      video: this.props.page.data ? this.props.page.data.landing_video : "",
    });
  }
}

